import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

class Box extends React.Component {
  selectBox = () => {
    this.props.selectBox(this.props.row, this.props.col);
  };

  render() {
    return (
      <div
        className={this.props.boxClass}
        id={this.props.id}
        onClick={this.selectBox}
      />
    );
  }
}

class Grid extends React.Component {
  render() {
    const width = this.props.cols * 16;
    let rowsArr = [];

    let boxClass = "";
    //the below loop will set an id and class for each cell in the grid(2d array)
    //id will be "i_j" and class will be either "box on"/"box off"
    for (let i = 0; i < this.props.rows; i++) {
      for (let j = 0; j < this.props.cols; j++) {
        let boxId = i + "_" + j;
        boxClass = this.props.gridFull[i][j] ? "box on" : "box off";
        rowsArr.push(
          <Box
            boxClass={boxClass}
            key={boxId}
            boxId={boxId}
            row={i}
            col={j}
            selectBox={this.props.selectBox}
          />
        );
      }
    }

    return (
      <div className="grid" style={{ width: width }}>
        {rowsArr}
      </div>
    );
  }
}

class Main extends React.Component {
  constructor() {
    super();
    this.speed = 100;
    this.rows = 30;
    this.cols = 50;

    this.state = {
      generation: 0,
      //this is gonna create a 2d array of 30x50 initialized with false
      gridFull: Array(this.rows)
        .fill()
        .map(() => Array(this.cols).fill(false)),
    };
  }

  selectBox = (row, col) => {
    //making a clone of the grid
    let gridCopy = arrayClone(this.state.gridFull);
    //switching the state
    gridCopy[row][col] = !gridCopy[row][col];
    //coping the new grid
    this.setState({
      gridFull: gridCopy,
    });
  };

  seed = () => {
    //setting the initial state
    let gridCopy = arrayClone(this.state.gridFull);
    //going through each grid and randomly choosing the initial condition(weather true/false)
    for(let i=0;i<this.rows;i++) {
      for(let j=0;j<this.cols;j++) {
        //gnerating a random number between 0-4
        //setting true if it's 1(20% chance of getting true)
        if(Math.floor(Math.random() * 5) === 1) gridCopy[i][j] = true;
      }
    }
    //coping the new grid
    this.setState({
      gridFull: gridCopy,
    });
  }

  playButton = () => {
    clearInterval(this.intervalId);
    this.intervalId = setInterval(this.play, this.speed);
  }

  play = () => {
    //first we make two copies of the grid. 
    //to compare and decide, the next generation
    let g = this.state.gridFull;
    let g2 = arrayClone(this.state.gridFull);

    //rules
    //#1 any live cell with fewer than two live neighbours dies,
    // as if by underpopulation
    //#2 any live cell with two or three live neighbours lives on to the next generation.
    //#3 any live cell with more than three live neighbours dies,
    //as if by underpopulation.
    //#4 any dead cell with exactly three live neighbours become a live cell,
    //as if by reproduction.

    for(let i=0;i<this.rows-1;i++) {
      for(let j=0;j<this.cols-1;j++) {
        let count = 0;
        //counting the live cells around each cell
        if(g[i-1][j-1]) count++;
        if(g[i-1][j]) count++;
        if(g[i-1][j+1]) count++;
        if(g[i][j-1]) count++;
        if(g[i][j+1]) count++;
        if(g[i+1][j-1]) count++;
        if(g[i+1][j]) count++;
        if(g[i+1][j+1]) count++;


      }
    }


  }

  componentDidMount() {
    this.seed();
  }

  render() {
    return (
      <div>
        <h1>The Game of Life</h1>
        <Grid
          gridFull={this.state.gridFull}
          rows={this.rows}
          cols={this.cols}
          selectBox={this.selectBox}
        />
        <h2>Generation: {this.state.generation}</h2>
      </div>
    );
  }
}

function arrayClone(arr) {
  return JSON.parse(JSON.stringify(arr));
}

ReactDOM.render(
  <React.StrictMode>
    <Main />
  </React.StrictMode>,
  document.getElementById("root")
);
