  } 
}

class Main extends React.Component {
  constructor() {
    super();
    this.speed = 100;
    this.rows = 30;
    this.cols = 50;

    this.state = {
      generation: 0,
      //this is gonna create a 2d array of 30x50 initialized with false
      gridFull: Array(this.rows).fill().map(() => 
      Array(this.cols).fill(false))
    }
  }

  render() {
    return (
      <div>
        <h1>The Game of Life</h1>
        <Grid gridFull={this.state.gridFull} rows={this.rows} cols={this.o} />
        <h2>Generation: {this.state.generation}</h2>
      </div>
    );
  }
}

ReactDOM.render(
  <React.StrictMode>
    <Main />
  </React.StrictMode>,
  document.getElementById("root")
